mapper.update:
update t_member_cert
set memberid = #{memberid,jdbcType=INTEGER},
  certid = #{certid,jdbcType=INTEGER},
  iconpath = #{iconpath,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, memberid, certid, iconpath
from t_member_cert
where id = #{id,jdbcType=INTEGER}
